import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Box,
  CardMedia,
  List,
  ListItem,
  Typography,
} from "@mui/material";
import React from "react";
import { BASE_URL } from "../API";

export default function Answer({ qnAnswers }) {
  return (
    <Box sx={{ mt: 5, width: "100%", maxWidth: 640, mx: "auto" }}>
      {qnAnswers.map((item, index) => (
        <Accordion disableGutters key={index}>
          <AccordionSummary>
            <Typography sx={{ width: "90%", flexShrink: 0 }}>
              {item.questionContent}
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
            {item.questionImage ? (
              <CardMedia
                component="img"
                image={BASE_URL + "images/" + item.questionImage}
                sx={{ m: "10px auto" }}
                // width: "auto",
              />
            ) : null}
            <List>
              {item.options.map((opt, idx) => {
                <ListItem key={idx}>
                  <Typography>
                    <b>{String.fromCharCode(65 + index) + ". "}</b>
                    {opt}
                  </Typography>
                </ListItem>;
              })}
            </List>
          </AccordionDetails>
        </Accordion>
      ))}
    </Box>
  );
}
