import React, { useEffect, useState } from "react";
import { ENDPOINTS, createAPIEndpoint } from "../API";
import {
  Box,
  Card,
  CardContent,
  CardHeader,
  LinearProgress,
  List,
  ListItemButton,
  Typography,
} from "@mui/material";
import { getFormatedTime } from "../Helper";
import useStateContext from "../Hooks/useStateContext";

export default function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [qnIndex, setQnIndex] = useState(0);
  const [timeTaken, setTimeTaken] = useState(0);

  const { context, setConText } = useStateContext();
  let timer;

  const startTimer = () => {
    timer = setInterval(() => {
      setTimeTaken((prev) => prev + 1);
    }, [1000]);
  };

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.question)
      .fetch()
      .then((res) => {
        setQuestions(res.data);
        startTimer();
      })
      .catch((err) => console.log(err));

    //return this function 'll invoke whenever we leave this component navigate to othes, reload
    return () => {
      clearInterval(timer);
    };
  }, []);

  const showAnswer = (questionId, optionsId) => {
    const temp = [...context.selectedOptions];
    // this state context is immutable bat bien, can not update
    temp.push({
      questionId,
      chosenOpt: optionsId,
    });

    setConText({ selectedOptions: [...temp] });
  };

  return questions.length === 0 ? null : (
    <Card
      sx={{
        maxWidth: 640,
        mx: "auto",
        mt: 5,
        "& .MuiCardHeader-action": { m: 0, alignSelf: "center" },
      }}
    >
      <CardHeader
        title={"Question " + (qnIndex + 1) + " of 5:"}
        action={<Typography>{getFormatedTime(timeTaken)}</Typography>}
      />
      <Box>
        <LinearProgress
          variant="determinate"
          value={((qnIndex + 1) / 5) * 100}
        />
      </Box>
      <CardContent>
        <Typography variant="h6">
          {questions[qnIndex].questionContent}
        </Typography>
        <List>
          {questions[qnIndex].options.map((item, index) => (
            <ListItemButton
              key={index}
              disableRipple
              onClick={() => showAnswer(questions[qnIndex].questionId, index)}
            >
              <div>
                <b>{String.fromCharCode(65 + index) + ". "}</b>
                {item}
              </div>
            </ListItemButton>
          ))}
        </List>
      </CardContent>
    </Card>
  );
}
